sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	if (!previous){ // when an employee is added to a Flamin' Burger Branch Employees - add the user to the Branch Emplyees Group
		InsertNewEmployee();
	}
	else if (previous) { //if the regional manager changes, remove the previos manager and add the new manager
		var grDelete = new GlideRecord("sys_user_grmember");
		grDelete.addQuery("user", previous.employee_name);
		grDelete.addQuery("group", 'bf392211dbe653003a915ec0cf9619db');
		grDelete.query();
		if (grDelete.next()) {
			grDelete.deleteRecord();
		}
		InsertNewEmployee();
	}
	function InsertNewEmployee(){
		var gr = new GlideRecord("sys_user_grmember");
		gr.initialize();
		gr.user = current.employee_name;
		gr.group = 'bf392211dbe653003a915ec0cf9619db'; //sys_id for x_103926_flamin_b.Branch Managers
		gr.insert();
	}

})(current, previous);
