sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var branch = current.branch;
	var item = current.item_ordered;
	var packSize = getPackSize();
	var quantityStr = current.u_quantity;
	var quantityInt = parseInt(quantityStr);
	var autoStock = branchAutoStock();

	if (autoStock == true){ //callback value of branch auto stock control
		var gr = new GlideRecord("x_103926_flamin_b_branch_inventory");
		gr.addQuery("branch", branch);
		gr.addQuery("item",item);
		gr.query();
		if (gr.next()) { //check to see if the branch already has previously recieved stock for this item
			var inventoryStr = gr.inventory_amount;
			var inventoryInt = parseInt(inventoryStr);
			var newTotal = (quantityInt*packSize) + inventoryInt;
			gr.inventory_amount = newTotal.toFixed(0); //add the order quantity to the branch inventory
			gr.update();
		}
		else if (!gr.next()){ //insert a record for the ordered item and add the ordered quantity to the inventory
			gr.initialize();
			gr.branch = branch;
			gr.item = item;
			gr.item_pack_size = packSize;
			gr.inventory_amount = quantityInt*packSize.toFixed(0);
			gr.insert();

		}
	}
	// check if branch has enabled auto stock control
	function branchAutoStock(){
		var grBranch = new GlideRecord("x_103926_flamin_b_fb_branches");
		grBranch.get(branch);
		if (grBranch.auto_stock_control == true){
			return true;
		}
		else {
			return false;
		}
	}
	// get the pack size for the item ordered
	function getPackSize(){
		var grItem = new GlideRecord("x_103926_flamin_b_fb_orderable_items");
		grItem.get(item);
		var size = grItem.getValue('pack_size');
		return size;
	}

})(current, previous);
