sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) { 
var elementID = gel("x_103926_flamin_b_fb_orders.order_status");
  switch(newValue) { 
    case "draft": elementID.style.backgroundColor = "red"; break; 
    case "pending": elementID.style.backgroundColor = "tomato"; break;
    case "ordered": elementID.style.backgroundColor = "orange"; break;
    case "delivery": elementID.style.backgroundColor = "yellow"; break;
    case "received": elementID.style.backgroundColor = "green"; break; 
    default: elementID.style.backgroundColor = "white"; break; } }
