sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var vat = 0.20;
   g_form.setValue('total_cost',subTotal());

	function calculatedCost(){
		var subTotal = subTotal();
		g_form.addErrorMessage(subTotal);
		var totalVat = subTotal*vat;
		return subTotal+totalVat;
	}

	function subTotal(){
		var quantity = g_form.getValue('u_quantity');
		var delivery = g_form.getValue('u_delivery_cost');
		var unitCost = g_form.getValue('u_cost_per_item');
		var total = quantity*delivery*unitCost;
		return total.toFixed(2);
	}



}
