sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var item = g_form.getReference('item_ordered',autoPop);

	function autoPop(item){
		g_form.setValue('u_supplier',item.supplier);
		g_form.setValue('u_cost_per_item',item.cost_per_item);
		var supplier = g_form.getReference('u_supplier',autoPop2);
		if (g_form.getValue('category') == ""){
			g_form.setValue('category',item.category);
		}
	}


	function autoPop2(supplier){
		g_form.setValue('u_delivery_cost',supplier.delivery_cost);
		g_form.addInfoMessage("Supplier and Cost Details added");
	}





}
