var itemUtils = Class.create();
itemUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getSupplierDetails: function(){
		gs.log("gp this has been called");
		var item = this.getParameter('sysparm_item');
		gs.log("gp "+ item);
		var supplier = {};
			var gr = new GlideRecord("");
			gr.get(item);
			gr.query();
			supplier.sys_id = gr.supplier;
		gs.log(" gp "+supplier.sys_id);
			supplier.cost = gr.delivery_cost;
			return JSON.stringify(supplier);
		},

		type: 'itemUtils'
	});
